template<class EventHandler>
void ParseEngine<EventHandler>::_reset()
{
    m_pending_anchors = {};
    m_pending_tags = {};
    if(m_options.locations())
    {
        _prepare_locations();
    }
    m_was_inside_qmrk = false;
}


